const (
	keyboardInterface = "wl_keyboard"
	keyboardVersion   = 7
)

// The wl_keyboard interface represents one or more keyboards
// associated with a seat.
type keyboardObject struct {
	id       uint32
	delete   func()
	listener interface {
		// This event provides a file descriptor to the client which can be
		// memory-mapped to provide a keyboard mapping description.
		//
		// From version 7 onwards, the fd must be mapped with MAP_PRIVATE by
		// the recipient, as MAP_SHARED may fail.
		Keymap(format uint32, fd *os.File, size uint32)

		// Notification that this seat's keyboard focus is on a certain
		// surface.
		//
		// The compositor must send the wl_keyboard.modifiers event after this
		// event.
		Enter(serial uint32, surface uint32, keys []byte)

		// Notification that this seat's keyboard focus is no longer on
		// a certain surface.
		//
		// The leave notification is sent before the enter notification
		// for the new focus.
		//
		// After this event client must assume that all keys, including modifiers,
		// are lifted and also it must stop key repeating if there's some going on.
		Leave(serial uint32, surface uint32)

		// A key was pressed or released.
		// The time argument is a timestamp with millisecond
		// granularity, with an undefined base.
		//
		// The key is a platform-specific key code that can be interpreted
		// by feeding it to the keyboard mapping (see the keymap event).
		//
		// If this event produces a change in modifiers, then the resulting
		// wl_keyboard.modifiers event must be sent after this event.
		Key(serial uint32, time uint32, key uint32, state uint32)

		// Notifies clients that the modifier and/or group state has
		// changed, and it should update its local state.
		Modifiers(serial uint32, modsDepressed uint32, modsLatched uint32, modsLocked uint32, group uint32)

		// Informs the client about the keyboard's repeat rate and delay.
		//
		// This event is sent as soon as the wl_keyboard object has been created,
		// and is guaranteed to be received by the client before any key press
		// event.
		//
		// Negative values for either rate or delay are illegal. A rate of zero
		// will disable any repeating (regardless of the value of delay).
		//
		// This event can be sent later on as well with a new value if necessary,
		// so clients should continue listening for the event past the creation
		// of wl_keyboard.
		RepeatInfo(rate int32, delay int32)
	}
}

func (obj keyboardObject) Dispatch(msg *wire.MessageBuffer) error {
	switch msg.Op() {
	case 0:
		format := msg.ReadUint()
		fd := msg.ReadFile()
		size := msg.ReadUint()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Keymap(
			format,
			fd,
			size,
		)
		return nil

	case 1:
		serial := msg.ReadUint()
		surface := msg.ReadUint()
		keys := msg.ReadArray()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Enter(
			serial,
			surface,
			keys,
		)
		return nil

	case 2:
		serial := msg.ReadUint()
		surface := msg.ReadUint()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Leave(
			serial,
			surface,
		)
		return nil

	case 3:
		serial := msg.ReadUint()
		time := msg.ReadUint()
		key := msg.ReadUint()
		state := msg.ReadUint()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Key(
			serial,
			time,
			key,
			state,
		)
		return nil

	case 4:
		serial := msg.ReadUint()
		modsDepressed := msg.ReadUint()
		modsLatched := msg.ReadUint()
		modsLocked := msg.ReadUint()
		group := msg.ReadUint()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Modifiers(
			serial,
			modsDepressed,
			modsLatched,
			modsLocked,
			group,
		)
		return nil

	case 5:
		rate := msg.ReadInt()
		delay := msg.ReadInt()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.RepeatInfo(
			rate,
			delay,
		)
		return nil
	}

	return wire.UnknownOpError{
		Interface: "wl_keyboard",
		Type:      "event",
		Op:        msg.Op(),
	}
}

