const (
	touchInterface = "wl_touch"
	touchVersion   = 7
)

// The wl_touch interface represents a touchscreen
// associated with a seat.
//
// Touch interactions can consist of one or more contacts.
// For each contact, a series of events is generated, starting
// with a down event, followed by zero or more motion events,
// and ending with an up event. Events relating to the same
// contact point can be identified by the ID of the sequence.
type touchObject struct {
	id       uint32
	delete   func()
	listener interface {
		// A new touch point has appeared on the surface. This touch point is
		// assigned a unique ID. Future events from this touch point reference
		// this ID. The ID ceases to be valid after a touch up event and may be
		// reused in the future.
		Down(serial uint32, time uint32, surface uint32, id int32, x wire.Fixed, y wire.Fixed)

		// The touch point has disappeared. No further events will be sent for
		// this touch point and the touch point's ID is released and may be
		// reused in a future touch down event.
		Up(serial uint32, time uint32, id int32)

		// A touch point has changed coordinates.
		Motion(time uint32, id int32, x wire.Fixed, y wire.Fixed)

		// Indicates the end of a set of events that logically belong together.
		// A client is expected to accumulate the data in all events within the
		// frame before proceeding.
		//
		// A wl_touch.frame terminates at least one event but otherwise no
		// guarantee is provided about the set of events within a frame. A client
		// must assume that any state not updated in a frame is unchanged from the
		// previously known state.
		Frame()

		// Sent if the compositor decides the touch stream is a global
		// gesture. No further events are sent to the clients from that
		// particular gesture. Touch cancellation applies to all touch points
		// currently active on this client's surface. The client is
		// responsible for finalizing the touch points, future touch points on
		// this surface may reuse the touch point ID.
		Cancel()

		// Sent when a touchpoint has changed its shape.
		//
		// This event does not occur on its own. It is sent before a
		// wl_touch.frame event and carries the new shape information for
		// any previously reported, or new touch points of that frame.
		//
		// Other events describing the touch point such as wl_touch.down,
		// wl_touch.motion or wl_touch.orientation may be sent within the
		// same wl_touch.frame. A client should treat these events as a single
		// logical touch point update. The order of wl_touch.shape,
		// wl_touch.orientation and wl_touch.motion is not guaranteed.
		// A wl_touch.down event is guaranteed to occur before the first
		// wl_touch.shape event for this touch ID but both events may occur within
		// the same wl_touch.frame.
		//
		// A touchpoint shape is approximated by an ellipse through the major and
		// minor axis length. The major axis length describes the longer diameter
		// of the ellipse, while the minor axis length describes the shorter
		// diameter. Major and minor are orthogonal and both are specified in
		// surface-local coordinates. The center of the ellipse is always at the
		// touchpoint location as reported by wl_touch.down or wl_touch.move.
		//
		// This event is only sent by the compositor if the touch device supports
		// shape reports. The client has to make reasonable assumptions about the
		// shape if it did not receive this event.
		Shape(id int32, major wire.Fixed, minor wire.Fixed)

		// Sent when a touchpoint has changed its orientation.
		//
		// This event does not occur on its own. It is sent before a
		// wl_touch.frame event and carries the new shape information for
		// any previously reported, or new touch points of that frame.
		//
		// Other events describing the touch point such as wl_touch.down,
		// wl_touch.motion or wl_touch.shape may be sent within the
		// same wl_touch.frame. A client should treat these events as a single
		// logical touch point update. The order of wl_touch.shape,
		// wl_touch.orientation and wl_touch.motion is not guaranteed.
		// A wl_touch.down event is guaranteed to occur before the first
		// wl_touch.orientation event for this touch ID but both events may occur
		// within the same wl_touch.frame.
		//
		// The orientation describes the clockwise angle of a touchpoint's major
		// axis to the positive surface y-axis and is normalized to the -180 to
		// +180 degree range. The granularity of orientation depends on the touch
		// device, some devices only support binary rotation values between 0 and
		// 90 degrees.
		//
		// This event is only sent by the compositor if the touch device supports
		// orientation reports.
		Orientation(id int32, orientation wire.Fixed)
	}
}

func (obj touchObject) Dispatch(msg *wire.MessageBuffer) error {
	switch msg.Op() {
	case 0:
		serial := msg.ReadUint()
		time := msg.ReadUint()
		surface := msg.ReadUint()
		id := msg.ReadInt()
		x := msg.ReadFixed()
		y := msg.ReadFixed()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Down(
			serial,
			time,
			surface,
			id,
			x,
			y,
		)
		return nil

	case 1:
		serial := msg.ReadUint()
		time := msg.ReadUint()
		id := msg.ReadInt()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Up(
			serial,
			time,
			id,
		)
		return nil

	case 2:
		time := msg.ReadUint()
		id := msg.ReadInt()
		x := msg.ReadFixed()
		y := msg.ReadFixed()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Motion(
			time,
			id,
			x,
			y,
		)
		return nil

	case 3:
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Frame()
		return nil

	case 4:
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Cancel()
		return nil

	case 5:
		id := msg.ReadInt()
		major := msg.ReadFixed()
		minor := msg.ReadFixed()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Shape(
			id,
			major,
			minor,
		)
		return nil

	case 6:
		id := msg.ReadInt()
		orientation := msg.ReadFixed()
		if err := msg.Err(); err != nil {
			return err
		}
		obj.listener.Orientation(
			id,
			orientation,
		)
		return nil
	}

	return wire.UnknownOpError{
		Interface: "wl_touch",
		Type:      "event",
		Op:        msg.Op(),
	}
}

