func (obj *DataDevice) Dispatch(msg *wire.MessageBuffer) error {
	switch msg.Op() {
	case 0:

		id := NewDataOffer(obj.state)
		id.SetID(msg.ReadUint())

		obj.state.Add(id)

		if err := msg.Err(); err != nil {
			return err
		}

		if obj.Listener == nil {
			return nil
		}
		obj.Listener.DataOffer(
			id,
		)
		return nil

	case 1:

		serial := msg.ReadUint()

		surface, _ := obj.state.Get(msg.ReadUint()).(*Surface)

		obj.state.Add(surface)

		x := msg.ReadFixed()

		y := msg.ReadFixed()

		id, _ := obj.state.Get(msg.ReadUint()).(*DataOffer)

		obj.state.Add(id)

		if err := msg.Err(); err != nil {
			return err
		}

		if obj.Listener == nil {
			return nil
		}
		obj.Listener.Enter(
			serial,
			surface,
			x,
			y,
			id,
		)
		return nil

	case 2:
		if err := msg.Err(); err != nil {
			return err
		}

		if obj.Listener == nil {
			return nil
		}
		obj.Listener.Leave()
		return nil

	case 3:

		time := msg.ReadUint()

		x := msg.ReadFixed()

		y := msg.ReadFixed()

		if err := msg.Err(); err != nil {
			return err
		}

		if obj.Listener == nil {
			return nil
		}
		obj.Listener.Motion(
			time,
			x,
			y,
		)
		return nil

	case 4:
		if err := msg.Err(); err != nil {
			return err
		}

		if obj.Listener == nil {
			return nil
		}
		obj.Listener.Drop()
		return nil

	case 5:

		id, _ := obj.state.Get(msg.ReadUint()).(*DataOffer)

		obj.state.Add(id)

		if err := msg.Err(); err != nil {
			return err
		}

		if obj.Listener == nil {
			return nil
		}
		obj.Listener.Selection(
			id,
		)
		return nil
	}

	return wire.UnknownOpError{
		Interface: "wl_data_device",
		Type:      "event",
		Op:        msg.Op(),
	}
}

