// primarily intended for use by generated code.
func NewShellSurface(state wire.State) *ShellSurface {
	return &ShellSurface{state: state}
}

func (obj *ShellSurface) State() wire.State {
	return obj.state
}

func (obj *ShellSurface) Dispatch(msg *wire.MessageBuffer) error {
	switch msg.Op() {
	case 0:

		serial := msg.ReadUint()

		if err := msg.Err(); err != nil {
			return err
		}

		if obj.Listener == nil {
			return nil
		}
		obj.Listener.Ping(
			serial,
		)
		return nil

	case 1:

		edges := ShellSurfaceResize(msg.ReadUint())

		width := msg.ReadInt()

		height := msg.ReadInt()

		if err := msg.Err(); err != nil {
			return err
		}

		if obj.Listener == nil {
			return nil
		}
		obj.Listener.Configure(
			edges,
			width,
			height,
		)
		return nil

	case 2:
		if err := msg.Err(); err != nil {
			return err
		}

		if obj.Listener == nil {
			return nil
		}
		obj.Listener.PopupDone()
		return nil
	}

	return wire.UnknownOpError{
		Interface: "wl_shell_surface",
		Type:      "event",
		Op:        msg.Op(),
	}
}

